import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LaunchPage.css';

function LaunchPage() {
  const [prompt, setPrompt] = useState('');
  const [genre, setGenre] = useState('');
  const [duration, setDuration] = useState(20);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitted:', { prompt, genre, duration });
    // Here you would typically send this data to your backend or process it
  };

  const genres = [
    'Pop', 'Rock', 'Hip Hop', 'Jazz', 'Classical', 'Electronic', 'Country'
  ];

  return (
    <div className="launch-page">
      <button className="back-btn" onClick={() => navigate('/')}>Back to Home</button>
      <form onSubmit={handleSubmit} className="launch-form">
        <h2>Create Your Music</h2>
        <div className="form-group">
          <label htmlFor="prompt">Enter your prompt:</label>
          <textarea
            id="prompt"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="genre">Select genre:</label>
          <select
            id="genre"
            value={genre}
            onChange={(e) => setGenre(e.target.value)}
            required
          >
            <option value="">Select a genre</option>
            {genres.map((g) => (
              <option key={g} value={g}>{g}</option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="duration">Duration: {duration} seconds</label>
          <input
            type="range"
            id="duration"
            min="10"
            max="30"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
        </div>
        <button type="submit" className="submit-btn">Generate Music</button>
      </form>
      
    </div>
  );
}

export default LaunchPage;
