import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './LaunchPage.css';

function LaunchPage() {
  const [prompt, setPrompt] = useState('');
  const [genre, setGenre] = useState('');
  const [duration, setDuration] = useState(20);
  const [musicId, setMusicId] = useState(null);
  const [saveMessage, setSaveMessage] = useState('');
  const [showNotification, setShowNotification] = useState(false); // State for notification visibility
  const navigate = useNavigate();

  useEffect(() => {
    createMusicNotes();
  }, []);

  const createMusicNotes = () => {
    const notes = ['â™©', 'â™ª', 'â™«', 'â™¬', 'â™­', 'ðŸŽ¼'];
    const background = document.querySelector('.music-background');

    for (let i = 0; i < 20; i++) {
      const note = document.createElement('div');
      note.className = 'music-note';
      note.textContent = notes[Math.floor(Math.random() * notes.length)];
      note.style.left = `${Math.random() * 100}%`;
      note.style.animationDuration = `${10 + Math.random() * 5}s`;
      note.style.animationDelay = `${Math.random() * 5}s`;
      background.appendChild(note);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitted:', { prompt, genre, duration });
    try {
      const response = await fetch('http://localhost:5000/api/music', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ prompt, genre, duration }),
      });

      if (response.ok) {
        const data = await response.json();
        setMusicId(data._id);
        console.log('Music data saved successfully:', data);

        setSaveMessage(`The following information was stored in the database:
                        Prompt: ${data.prompt},
                        Genre: ${data.genre},
                        Duration: ${data.duration} seconds`);
        
        // Show notification
        setShowNotification(true);

        // Hide notification after 3 seconds
        setTimeout(() => setShowNotification(false), 3000);
      } else {
        console.error('Failed to save music data');
      }
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleMusicGeneration = (type) => {
    console.log(`Generating ${type} music...`);
  };

  const handleBack = () => {
    navigate(-1);
  };

  const genres = [
    'Pop',
    'Rock',
    'Hip Hop',
    'Jazz',
    'Classical',
    'Electronic',
    'Country',
  ];

  return (
    <div className="launch-page">
      <div className="music-background"></div>
      <button className="back-btn" onClick={handleBack}>ðŸ¢€</button>

      <form className="launch-form" onSubmit={handleSubmit}>
        <h2>Create Your Music</h2>

        <div className="form-group">
          <label htmlFor="prompt">Prompt:</label>
          <textarea
            id="prompt"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label htmlFor="genre">Genre:</label>
          <select
            id="genre"
            value={genre}
            onChange={(e) => setGenre(e.target.value)}
          >
            <option value="">Select a genre</option>
            {genres.map((g) => (
              <option key={g} value={g}>
                {g}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="duration">Duration: {duration} seconds</label>
          <input
            type="range"
            id="duration"
            min="10"
            max="30"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
        </div>

        <div className="form-group">
          <button type="submit" className="submit-btn">
            Create Music
          </button>
        </div>
      </form>

      {showNotification && (
        <div className="notification">
          {saveMessage}
        </div>
      )}

      {musicId && (
        <div className="music-player">
          <h3>Generated Music</h3>
          <audio controls>
            <source src={`http://localhost:5000/music/${musicId}`} type="audio/mpeg" />
            Your browser does not support the audio element.
          </audio>
        </div>
      )}

      <div className="image-holders">
        <div className="image-holder">
          <img src="happy.png" alt="Happy Music" />
          <button onClick={() => handleMusicGeneration('happy')}>
            Generate Happy Music
          </button>
        </div>

        <div className="image-holder">
          <img src="soothing.png" alt="Soothing Music" />
          <button onClick={() => handleMusicGeneration('soothing')}>
            Generate Soothing Music
          </button>
        </div>

        <div className="image-holder">
          <img src="party.png" alt="Party Music" />
          <button onClick={() => handleMusicGeneration('party')}>
            Generate Party Music
          </button>
        </div>
      </div>
    </div>
  );
}

export default LaunchPage;
