import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LaunchPage.css';

function LaunchPage() {
  const [prompt, setPrompt] = useState('');
  const [genre, setGenre] = useState('');
  const [duration, setDuration] = useState(20);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Submitted:', { prompt, genre, duration });
    // Here you would typically send this data to your backend or process it
  };

  const handleMusicGeneration = (type) => {
    console.log(`Generating ${type} music...`);
  };

  const handleBack = () => {
    navigate(-1); // Navigate back to the previous page
  };

  const genres = [
    'Pop',
    'Rock',
    'Hip Hop',
    'Jazz',
    'Classical',
    'Electronic',
    'Country',
  ];

  return (
    <div className="launch-page">
      <button className="back-btn" onClick={handleBack}>Back</button>

      <form className="launch-form" onSubmit={handleSubmit}>
        <h2>Create Your Music</h2>

        <div className="form-group">
          <label htmlFor="prompt">Prompt:</label>
          <textarea
            id="prompt"
            value={prompt}
            onChange={(e) => setPrompt(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label htmlFor="genre">Genre:</label>
          <select
            id="genre"
            value={genre}
            onChange={(e) => setGenre(e.target.value)}
          >
            {genres.map((g) => (
              <option key={g} value={g}>
                {g}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="duration">Duration: {duration} seconds</label>
          <input
            type="range"
            id="duration"
            min="10"
            max="30"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
        </div>

        <div className="form-group">
          <button type="submit" className="submit-btn">
            Create Music
          </button>
        </div>
      </form>

      <div className="image-holders">
        <div className="image-holder">
          <img src="happy-music.jpg" alt="Happy Music" />
          <button onClick={() => handleMusicGeneration('happy')}>
            Generate Happy Music
          </button>
          
        </div>

        <div className="image-holder">
          <img src="soothing-music.jpg" alt="Soothing Music" />
          <button onClick={() => handleMusicGeneration('soothing')}>
            Generate Soothing Music
          </button>
        </div>

        <div className="image-holder">
          <img src="party-music.jpg" alt="Party Music" />
         
          <button onClick={() => handleMusicGeneration('party')}>
            Generate Party Music
          </button>
        </div>
      </div>
    </div>
  );
}

export default LaunchPage;
